USE master drop database QL_PRODUCTS create database QL_PRODUCTS use QL_PRODUCTS  ------- Tạo bảng  create table TB_GUESTS ( 	TEN_KH NVARCHAR(50) NOT NULL, 	EMAIL NVARCHAR(50)  NULL, 	DIACHI NVARCHAR(100) NOT NULL, 	NOTE NVARCHAR(100) NOT NULL ) create table TB_Category ( 	MaLoai nvarchar(20) not null, 	Loaihang nvarchar(50) not null 	CONSTRAINT PK_MaLoai PRIMARY KEY (MaLoai) ) create table TB_SanPham ( 	MA_SPHAM NVARCHAR(20) NOT NULL, 	MaLoai nvarchar(20) not null, 	Ten_SP NVARCHAR (50) NULL, 	Hinh varchar(50) null, 	DT_DEN NVARCHAR (50) NULL, 	DT_PIN NVARCHAR (50) NULL, 	CONG_SUAT_DEN NVARCHAR(10) NOT NULL, 	SL_CHIPLEAD NVARCHAR (10) NOT NULL, 	CONG_SUAT_PIN NVARCHAR(10) NOT NULL, 	DUNG_LUONG_PIN NVARCHAR(10) NOT NULL, 	LIGHT_RANGE NVARCHAR(20) NOT NULL, 	LIGHT_TIME NVARCHAR(20) NOT NULL,  	PRIDE Int NOT NULL, 	CONSTRAINT PK_MA_SP PRIMARY KEY (MA_SPHAM) ) CREATE TABLE TB_DONDATHANG ( 	MADON NVARCHAR(20) NOT NULL, 	TEN_KH NVARCHAR(50) NULL, 	USER_PHONE NVARCHAR(50) NULL, 	AMOUNT NVARCHAR(50) NOT NULL, 	MESSEAGE NVARCHAR(100) NULL, 	CONSTRAINT PK_TB_GIAODICH PRIMARY KEY(MADON) ) CREATE TABLE TB_CTDH ( 	MACTDH NVARCHAR(20) NOT NULL, 	MA_GD NVARCHAR(20) NOT NULL, 	MA_SP NVARCHAR(20) NOT NULL, 	SO_LUONG INT NOT NULL, 	DON_GIA INT NOT NULL, 	CONSTRAINT PK_MACTDH PRIMARY KEY (MACTDH ASC,MA_SP) ) ------- TẠO CÁC KHOÁ NGOẠI ALTER TABLE TB_CTDH ADD CONSTRAINT PK_CTDH_SanPham FOREIGN KEY (MA_SP) REFERENCES TB_SanPham(MA_SPHAM) ALTER TABLE TB_SanPham ADD CONSTRAINT PK_SanPham_Category FOREIGN KEY (MaLoai) REFERENCES TB_Category(MaLoai) ALTER TABLE TB_CTDH ADD CONSTRAINT PK_CTDH_GIAODICH FOREIGN KEY (MA_GD) REFERENCES TB_DONDATHANG(MADON)  INSERT INTO TB_Category VALUES (1,N'Đèn pha năng lượng mặt trời JINDIAN') INSERT INTO TB_Category VALUES (2,N'Đèn đường năng lượng mặt trời JINDIAN')  INSERT INTO TB_SanPham VALUES('JD-8825L',1,N'Đèn JD-8825L','JD-8825L.jpg',N'210x160mm',N'175x340mm',N'25W',N'72 Led',N'12W',N'9.6Ah',N'60m2',N'10-14h',585000) INSERT INTO TB_SanPham VALUES('JD